package cat.uvic.teknos.m09;

import cat.uvic.teknos.m09.Exceptions.MissingPropertiesException;

import java.io.FileNotFoundException;
import java.io.IOException;
import java.security.MessageDigest;
import java.security.NoSuchAlgorithmException;
import java.security.SecureRandom;
import java.util.Base64;
import java.util.HashMap;
import java.util.Properties;

public class Hash {
    Properties props;

    public  Hash() throws IOException {
        this.props = new Properties();
        //takes file properties from current classpath
        try{
            this.props.load(ClassLoader.getSystemClassLoader().getResourceAsStream("/Hash.properties"));
        }catch(IOException| NullPointerException error){
            setDefaultProperties();
        }
    }

    public byte[] getHash(byte[] message) throws NoSuchAlgorithmException, MissingPropertiesException {

        String algorithm = this.props.getProperty("algorithm");

        if(algorithm == null){
            throw new MissingPropertiesException();
        }

        var messageDigest = MessageDigest.getInstance(algorithm);
        var salt  = this.props.getProperty("salt");

        if(salt!=null){
            messageDigest.update(salt.getBytes());
        }

        return messageDigest.digest(message);
    }

    public  String getHashAsString(byte[] message) throws NoSuchAlgorithmException, MissingPropertiesException {
        String algorithm = this.props.getProperty("algorithm");

        if(algorithm == null){
            throw new MissingPropertiesException();
        }

        var messageDigest = MessageDigest.getInstance(algorithm);
        var salt  = this.props.getProperty("salta");

        if(salt!=null){
            messageDigest.update(salt.getBytes());
        }

        var digest = messageDigest.digest(message);
        var base64Encoder = Base64.getEncoder();
        return base64Encoder.encodeToString(digest);

    }

    public HashMap<String,byte[]> getHashWithRandomSalt(byte[] message) throws MissingPropertiesException, NoSuchAlgorithmException {
        HashMap<String,byte[]> result = new HashMap<>();

        String algorithm = this.props.getProperty("algorithm");

        if(algorithm == null){
            throw new MissingPropertiesException();
        }

        var messageDigest = MessageDigest.getInstance(algorithm);
        var salt  = getRandomSalt();

        messageDigest.update(salt);

        result.put("hash",messageDigest.digest(message));
        result.put("salt",salt);

        return result;
    }

    public HashMap<String,String> getHashWithRandomSaltAsString(byte[] message) throws MissingPropertiesException, NoSuchAlgorithmException {
        HashMap<String,String> result = new HashMap<>();

        String algorithm = this.props.getProperty("algorithm");

        if(algorithm == null){
            throw new MissingPropertiesException();
        }

        var messageDigest = MessageDigest.getInstance(algorithm);
        var salt  = getRandomSalt();

        messageDigest.update(salt);

        var digest = messageDigest.digest(message);
        var base64Encoder = Base64.getEncoder();
        String hashResult = base64Encoder.encodeToString(digest);

        result.put("hash",hashResult);
        result.put("salt",new String(salt));

        return result;
    }

    //takes file properties from Library resources
    public void setDefaultProperties() throws IOException {
        this.props.load(Hash.class.getResourceAsStream("/Hash.properties"));
    }

    public Properties getProps(){
        return this.props;
    }

    public static byte[] getRandomSalt(){
        var secureRandom = new SecureRandom();
        var salt  = new byte[16];
        secureRandom.nextBytes(salt);
        return salt;
    }
}
